{"schemaVersion": "1.0.0",
    "class": "Device",
    "async": true,
    "label": "Example 1NIC BIG-IP with Runtime-Init",
    "Common": {
      "class": "Tenant",
      "My_DbVariables": {
        "class": "DbVariables",
        "ui.advisory.enabled": true,
        "ui.advisory.color": "red",
        "ui.advisory.text": "Configuration deployed with AS3. Do not make any change directly on the BIG-IP or those changes may be lost."

      },
      "My_System": {
        "class": "System",
        "hostname": "agy-demo-1nic-0a73-f5vm01.local",
        "cliInactivityTimeout": 1200,
        "consoleInactivityTimeout": 1200,
        "autoPhonehome": false,
        "autoCheck": true,
        "tmshAuditLog": true,
        "guiAuditLog": false,
        "mcpAuditLog": "enable",
        "preserveOrigDhcpRoutes": false,
        "guiSecurityBanner": true,
        "guiSecurityBannerText": "Welcome to the BIG-IP Configuration Utility.\n\nLog in with your username and password using the fields on the left.",
        "usernamePrompt": "Username",
        "passwordPrompt": "Password"
      },
      "myNtp": {
        "class": "NTP",
        "servers": [
          "0.pool.ntp.org"
        ],
        "timezone": "CET"
      },
      "myDns": {
        "class": "DNS",
        "nameServers": [
          "168.63.129.16"
        ]
      },
      "myProvisioning": {
        "class": "Provision",
        "ltm": "nominal",
        "asm": "nominal"
      },
      "admin": {
        "class": "User",
        "partitionAccess": {
          "all-partitions": {
            "role": "admin"
          }
        },
        "shell": "bash",
        "keys": [
          "ssh-rsa  a.gyllenhammar@X64K2JX6N7 "
        ],
        "userType": "regular",
        "forceInitialPasswordChange": false
      },
      "bigipuser": {
        "class": "User",
        "partitionAccess": {
          "all-partitions": {
            "role": "admin"
          }
        },
        "shell": "bash",
        "keys": [
          "ssh-rsa  a.gyllenhammar@X64K2JX6N7 "
        ],
        "userType": "regular",
        "forceInitialPasswordChange": false
      }
    }
  }

{
  "class": "AS3",
  "action": "deploy",
  "persist": true,
  "declaration": {
    "class": "ADC",
    "schemaVersion": "3.0.0",
    "id": "urn:uuid:33045210-3ab8-4636-9b2a-c98d22ab915d",
    "label": "Sample 1",
    "remark": "Simple HTTP Service with Round-Robin Load Balancing",
    "Sample_01": {
      "class": "Tenant",
      "A1": {
        "class": "Application",
        "service": {
          "class": "Service_HTTP",
          "virtualAddresses": [
            "10.0.1.10"
          ],
          "pool": "web_pool"
        },
        "web_pool": {
          "class": "Pool",
          "monitors": [
            "http"
          ],
          "members": [
            {
              "servicePort": 80,
              "serverAddresses": [
                "192.0.1.10",
                "192.0.1.11"
              ]
            }
          ]
        }
      }
    }
  }
}

{
  "class": "AS3",
  "action": "deploy",
  "persist": true,
  "declaration": {
    "class": "ADC",
    "schemaVersion": "3.0.0",
    "id": "urn:uuid:33045210-3ab8-4636-9b2a-c98d22ab915d",
    "label": "Sample 1",
    "remark": "Simple HTTP Service with Round-Robin Load Balancing",
    "Sample_01": {
      "class": "Tenant",
      "A2": {
        "class": "Application",
        "service": {
          "class": "Service_HTTP",
          "virtualAddresses": [
            "10.0.100.2"
          ],
          "pool": "web_pool"
        },
        "web_pool": {
          "class": "Pool",
          "monitors": [
            "http"
          ],
          "members": [
            {
              "servicePort": 80,
              "serverAddresses": [
                "192.10.1.10",
                "192.10.1.11"
              ]
            }
          ]
        }
      }
    }
  }
}
